import dayjs from "dayjs";
import {message} from "antd";
import {useEffect} from "react";

const TOKEN_STORAGE_KEY = {
    AUTH: "authToken",
    REFRESH: "refreshToken",
    USER: "user",
    ISADMINPAGE: "isAdminPage",
    INDEXTAB: "indexTab",
};
export const getTokens = () => ({
  authToken: localStorage.getItem(TOKEN_STORAGE_KEY.AUTH),
  user: JSON.parse(localStorage.getItem(TOKEN_STORAGE_KEY.USER)),
  // refreshToken: localStorage.getItem(JSON.parse(TOKEN_STORAGE_KEY.REFRESH)),
});

export const getIsAdminPage = () => ({
  isAdminPage: JSON.parse(localStorage.getItem(TOKEN_STORAGE_KEY.ISADMINPAGE)),
});

export const setTokens = (authToken, user) => {
    localStorage.setItem(TOKEN_STORAGE_KEY.AUTH, authToken);
    localStorage.setItem(TOKEN_STORAGE_KEY.USER, JSON.stringify(user));
};


export const numberFormat = (num) => {
   return num.toLocaleString('vi-VN', {style : 'currency', currency : 'VND'});
}

export const removeTokens = () => {
    localStorage.removeItem(TOKEN_STORAGE_KEY.AUTH);
    localStorage.removeItem(TOKEN_STORAGE_KEY.USER);
    localStorage.removeItem(TOKEN_STORAGE_KEY.REFRESH);
    localStorage.removeItem(TOKEN_STORAGE_KEY.ISADMINPAGE);
    localStorage.removeItem(TOKEN_STORAGE_KEY.INDEXTAB);
};

export function getMonth(month = dayjs().month()) {
  month = Math.floor(month);
  const year = dayjs().year();
  const firstDayOfTheMonth = dayjs(new Date(year, month, 1)).day();
  let currentMonthCount = 0 - firstDayOfTheMonth;
  const daysMatrix = new Array(5).fill([]).map(() => {
    return new Array(7).fill(null).map(() => {
      currentMonthCount++;
      return dayjs(new Date(year, month, currentMonthCount));
    });
  });
  return daysMatrix;
}

// messageUtils.js
export function useMessageApi() {
    const [messageApi, contextHolder] = message.useMessage();

    function showSuccessMessage(content) {
        messageApi.success(content);
    }

    function showErrorMessage(content) {
        messageApi.error(content);
    }

    // Các hàm khác liên quan đến messageApi
    return { messageApi, contextHolder, showSuccessMessage, showErrorMessage };
}

export const imageDefault = () => {
    return 'data:image/png;base64,'
}

export function convertToSlug(text) {
    return text.toLowerCase()
        .replace(/ /g,'-')        // Thay thế khoảng trắng bằng gạch ngang
        .replace(/[^\p{L}\d-]/gu, '') // Loại bỏ ký tự không phải là chữ cái, số, hoặc dấu gạch ngang
        .normalize('NFD')         // Chuyển đổi các ký tự có dấu thành ký tự không dấu
        .replace(/[\u0300-\u036f]/g, ''); // Loại bỏ các dấu thanh
}